mapper.select:

select rbno, pseq, writer, title, content, star, regdate, pname 
from(
select rownum, rbno, p.pseq, writer, title, r.content, star, regdate, p.name as pname
from review r, product p where r.pseq=p.pseq order by rbno desc) 
where rownum <7


select 
	rbno, title, content, writer, regdate, star, pname, kind
from (
	select rownum rn, rbno, title, r.content, writer, regdate, star, p.name as pname, p.kind 
	from review r, product p 
	where 




	r.pseq = p.pseq and rownum <= #{pageNum} * #{amount} order by regdate desc
)
where rn > (#{pageNum} - 1) * #{amount}


select oseq from(select distinct rownum rn, oseq from order_view 
where status=#{status} and rownum <= #{pageNum} * #{amount} order by oseq desc) 
where rn  > (#{pageNum} - 1) * #{amount}
