mapper.select:

select 
	pseq, name, kind, price1, price2, price3, content, image, bestyn, indate 
from (
	select rownum rn, pseq, name, kind, price1, price2, price3, content, image, bestyn, indate 
	from product 
	where 




	rownum <= #{pageNum} * #{amount} order by pseq desc
)
where rn > (#{pageNum} - 1) * #{amount}


select 
   pseq, name, kind, price2, content, image 
from (
   select rownum rn, pseq, name, kind, price2, content, image  
   from product
   where 

   rownum <= #{pageNum} * #{amount} order by pseq desc
)
where rn > (#{pageNum}-1) * #{amount}
